import Vue from 'vue';
import Router from 'vue-router';
import Home from './views/Home.vue';
import Products from './views/Products.vue';
import Orders from './views/Orders.vue';

Vue.use(Router);

const routes = [
    {
        path: '/',
        name: 'home',
        component: Home
    },
    {
        path: '/products',
        name: 'products',
        component: Products
    },
    {
        path: '/orders',
        name: 'orders',
        component: Orders
    }
];

const router = new Router({
    mode: 'history',
    routes
});

export default router;
