export default {
  props: {
    product: {
      type: Object,
      default: () => ({})
    },
    modelValue: {
      // 使用 v-model
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      visible: this.modelValue,
      // 使用 modelValue
      form: {
        productName: '',
        price: 0,
        status: 'active'
      }
    };
  },
  watch: {
    product: {
      immediate: true,
      handler(newVal) {
        if (newVal) {
          this.form = {
            ...newVal
          };
        }
      }
    },
    modelValue: {
      immediate: true,
      handler(newVal) {
        this.visible = newVal; // 监听 modelValue 的变化
      }
    }
  },
  methods: {
    submitForm() {
      const method = this.product ? 'PUT' : 'POST';
      const url = this.product ? `http://localhost:8080/api/products/${this.product.productId}` : 'http://localhost:8080/api/products';
      fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(this.form)
      }).then(() => {
        this.$emit('refresh');
        this.handleClose();
      });
    },
    handleClose() {
      this.visible = false;
      this.$emit('close');
    }
  }
};
