import Vue from 'vue';
import Router from 'vue-router';
import HomePage from './views/Home.vue'; // 更新为新名称
import ProductsPage from './views/Products.vue'; // 更新为新名称
import OrdersPage from './views/Orders.vue'; // 更新为新名称

Vue.use(Router);

const routes = [
    {
        path: '/',
        name: 'home',
        component: HomePage // 更新为新名称
    },
    {
        path: '/products',
        name: 'products',
        component: ProductsPage // 更新为新名称
    },
    {
        path: '/orders',
        name: 'orders',
        component: OrdersPage // 更新为新名称
    }
];

const router = new Router({
    mode: 'history',
    routes
});

export default router;
