        'COMPLETED': 'Â∑≤ÂÆåÊàê',
        'CANCELLED': 'Â∑≤ÂèñÊ∂à'
      };
      return statusMap[order.status] || order.status;
    };

    // Ëé∑ÂèñËÆ¢ÂçïÁä∂ÊÄÅCSSÁ±ª
    const getStatusClass = order => {
      if (!order || !order.status) return '';
      const statusClassMap = {
        'PENDING': 'status-pending',
        'PROCESSING': 'status-processing',
        'SHIPPED': 'status-shipped',
        'DELIVERED': 'status-delivered',
        'COMPLETED': 'status-completed',
        'CANCELLED': 'status-cancelled'
      };
      return statusClassMap[order.status] || '';
    };
    return {
      // Ë°®ÂçïÂèÇÊï∞
      merchantId,
      selectedProductId,
      daysToPredict,
      selectedView,
      // Áä∂ÊÄÅÊéßÂà∂
      loading,
      loadingProducts,
      error,
      formError,
      showResults,
      // Êï∞ÊçÆ
      products,
      productOrders,
      predictionData,
      // ËÆ°ÁÆóÂ±ûÊÄß
      isFormValid,
      selectedProductName,
      chartData,
      chartOptions,
      // ÊñπÊ≥ï
      fetchProducts,
      startPrediction,
      viewProductPrediction,
      calculateAverage,
      calculateSum,
      calculateTrend,
      formatDate,
      getOrderQuantity,
      getStatusText,
      getStatusClass
    };
  }
};

