    // ÂõæË°®Êï∞ÊçÆ
    const chartData = computed(() => {
      return {
        labels: predictionData.dates,
        datasets: [{
          label: selectedView.value === 'all' ? 'ÊâÄÊúâ‰∫ßÂìÅÈîÄÂîÆÈ¢ÑÊµã' :
              (selectedProductName.value ? `${selectedProductName.value} ÈîÄÂîÆÈ¢ÑÊµã` : `‰∫ßÂìÅ #${selectedProductId.value} ÈîÄÂîÆÈ¢ÑÊµã`),
