    // ÂõæË°®ÈÄâÈ°π
    const chartOptions = {
      responsive: true,
      maintainAspectRatio: false,
      scales: {
        y: {
          beginAtZero: false,
          title: {
            display: true,
            text: 'ÈîÄÂîÆÈáëÈ¢ù (¬•)',
            font: {
              size: 14,
