            }
          },
          grid: {
            color: 'rgba(0, 0, 0, 0.05)'
          }
        },
        x: {
          title: {
            display: true,
            text: 'Êó•Êúü',
            font: {
              size: 14,
              weight: 'bold'
            }
          },
          grid: {
            color: 'rgba(0, 0, 0, 0.05)'
          }
        }
      },
      plugins: {
        tooltip: {
          backgroundColor: 'rgba(0, 0, 0, 0.7)',
          titleFont: {
            size: 14
          },
          bodyFont: {
            size: 13
          },
          callbacks: {
            label: function(context) {
              return `ÈîÄÂîÆÈ¢ù: ¬•${context.raw.toFixed(2)}`;
            }
          }
        },
        legend: {
          position: 'top',
          labels: {
            font: {
              size: 14
            }
          }
        }
      }
    };

    // Ëé∑ÂèñÊâÄÊúâ‰∫ßÂìÅÈîÄÂîÆÈ¢ÑÊµã
    const fetchAllProductsPredictions = async () => {
      loading.value = true;
      error.value = null;

      try {
        const response = await axios.get(`http://localhost:8080/api/ai/sales-prediction/${merchantId.value}`);

        // Êõ¥Êñ∞È¢ÑÊµãÊï∞ÊçÆ
        predictionData.dates = response.data.dates;
        predictionData.predictions = response.data.predictions;

        // Ê†áËÆ∞ÊòæÁ§∫ÁªìÊûú
        showResults.value = true;
      } catch (err) {
        console.error('Ëé∑ÂèñÈ¢ÑÊµãÊï∞ÊçÆÂ§±Ë¥•:', err);
        error.value = err.response?.data?.message || 'Êó†Ê≥ïËé∑ÂèñÈ¢ÑÊµãÊï∞ÊçÆÔºåËØ∑Ê£ÄÊü•ÂïÜÂÆ∂IDÊòØÂê¶Ê≠£Á°Æ';
      } finally {
        loading.value = false;
      }
    };

    // Ëé∑ÂèñÂçï‰∏™‰∫ßÂìÅÈîÄÂîÆÈ¢ÑÊµã
    const fetchProductPredictions = async () => {
      if (!selectedProductId.value) {
        error.value = 'ËØ∑ÈÄâÊã©Ë¶ÅÈ¢ÑÊµãÁöÑ‰∫ßÂìÅ';
        return;
      }

      loading.value = true;
      error.value = null;

      try {
        const response = await axios.get(
            `http://localhost:8080/api/ai/sales-prediction-by-product/${merchantId.value}/${selectedProductId.value}/${daysToPredict.value}`
        );

        // Êõ¥Êñ∞È¢ÑÊµãÊï∞ÊçÆ
        predictionData.dates = response.data.dates;
        predictionData.predictions = response.data.predictions;

        // Ëé∑Âèñ‰∫ßÂìÅÁöÑÂéÜÂè≤ËÆ¢Âçï
        await fetchProductOrders();

        // Ê†áËÆ∞ÊòæÁ§∫ÁªìÊûú
        showResults.value = true;
      } catch (err) {
        console.error('Ëé∑Âèñ‰∫ßÂìÅÈ¢ÑÊµãÊï∞ÊçÆÂ§±Ë¥•:', err);
        error.value = err.response?.data?.message || 'Êó†Ê≥ïËé∑Âèñ‰∫ßÂìÅÈ¢ÑÊµãÊï∞ÊçÆ';
      } finally {
        loading.value = false;
      }
    };

// ‰øÆÊîπÂêéÁöÑfetchProductsÊñπÊ≥ï:
    const fetchProducts = async () => {
      console.log("ÂºÄÂßãËé∑Âèñ‰∫ßÂìÅÂàóË°®ÔºåÂïÜÂÆ∂ID:", merchantId.value);

      if (!merchantId.value) {
        formError.value = 'ËØ∑ÂÖàËæìÂÖ•ÂïÜÂÆ∂ID';
        return;
      }

      loadingProducts.value = true;
      formError.value = null;

      try {
        // Á°Æ‰øùËØ∑Ê±ÇÂåÖÂê´Ê≠£Á°ÆÁöÑheaders
        console.log("ÂèëÈÄÅËØ∑Ê±ÇÂà∞:", `http://localhost:8080/api/products`);
        const response = await axios.get(`http://localhost:8080/api/products`, {
          headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json'
          }
        });

        console.log("Ëé∑Âèñ‰∫ßÂìÅÂàóË°®ÂìçÂ∫î:", response);

        // Á°Æ‰øùresponse.dataÂ≠òÂú®‰∏îÊòØÊï∞ÁªÑ
        if (!response.data || !Array.isArray(response.data)) {
          throw new Error('ËøîÂõûÁöÑÊï∞ÊçÆÊ†ºÂºè‰∏çÊ≠£Á°Æ');
        }

        console.log("Ëé∑ÂèñÂà∞‰∫ßÂìÅÊï∞Èáè:", response.data.length);

        // ËøáÊª§ÂïÜÂÆ∂Áõ∏ÂÖ≥ÁöÑ‰∫ßÂìÅ
        const merchantProducts = response.data.filter(product => {
          console.log("Ê£ÄÊü•‰∫ßÂìÅ:", product, "ÂïÜÂÆ∂ID:", merchantId.value);
          return product.merchantId === merchantId.value;
        });

        console.log("ËøáÊª§ÂêéÁöÑÂïÜÂÆ∂‰∫ßÂìÅ:", merchantProducts);

        // Â¶ÇÊûúÊ≤°ÊúâÊâæÂà∞ÂΩìÂâçÂïÜÂÆ∂ÁöÑ‰∫ßÂìÅÔºåÊòæÁ§∫ÊâÄÊúâ‰∫ßÂìÅ
        if (merchantProducts.length === 0) {
          products.value = response.data;
          formError.value = 'Êú™ÊâæÂà∞ËØ•ÂïÜÂÆ∂ÁöÑ‰∫ßÂìÅÔºåÂ∑≤ÊòæÁ§∫ÊâÄÊúâ‰∫ßÂìÅ';
        } else {
          products.value = merchantProducts;
        }
      } catch (err) {
        console.error('Ëé∑Âèñ‰∫ßÂìÅÂàóË°®Â§±Ë¥•:', err);
        // Êèê‰æõÊõ¥ËØ¶ÁªÜÁöÑÈîôËØØ‰ø°ÊÅØ
        formError.value = `Êó†Ê≥ïËé∑Âèñ‰∫ßÂìÅÂàóË°®: ${err.message || 'ËØ∑Ê£ÄÊü•ÁΩëÁªúËøûÊé•ÂíåÊúçÂä°Âô®Áä∂ÊÄÅ'}`;
      } finally {
        loadingProducts.value = false;
      }
    };

    // Ëé∑Âèñ‰∫ßÂìÅÁöÑÂéÜÂè≤ËÆ¢Âçï
    const fetchProductOrders = async () => {
      try {
        // Ëé∑ÂèñËÆ¢ÂçïÂàóË°®
        const response = await axios.get(`http://localhost:8080/api/orders/${merchantId.value}`);

        // ËøáÊª§‰∏éÊâÄÈÄâ‰∫ßÂìÅÁõ∏ÂÖ≥ÁöÑËÆ¢Âçï
        productOrders.value = response.data.filter(order => {
          return order.merchantId === merchantId.value;
          // Â¶ÇÊûúËÆ¢Âçï‰∏≠ÂåÖÂê´‰∫ßÂìÅ‰ø°ÊÅØÔºåÂàôÂèØ‰ª•Ëøõ‰∏ÄÊ≠•ËøáÊª§
          // return order.merchantId === merchantId.value && order.productId === selectedProductId.value;
        });
      } catch (err) {
        console.error('Ëé∑Âèñ‰∫ßÂìÅËÆ¢ÂçïÂ§±Ë¥•:', err);
        productOrders.value = [];
      }
    };

    // ÂºÄÂßãÈ¢ÑÊµã
    const startPrediction = () => {
      formError.value = null;

      if (!merchantId.value) {
        formError.value = 'ËØ∑ËæìÂÖ•ÂïÜÂÆ∂ID';
        return;
      }

      if (selectedView.value === 'product' && !selectedProductId.value) {
        formError.value = 'ËØ∑ÈÄâÊã©Ë¶ÅÈ¢ÑÊµãÁöÑ‰∫ßÂìÅ';
        return;
      }

      if (selectedView.value === 'all') {
        fetchAllProductsPredictions();
      } else {
        fetchProductPredictions();
      }
    };

    // Êü•ÁúãÁâπÂÆö‰∫ßÂìÅÁöÑÈ¢ÑÊµã
    const viewProductPrediction = (productId) => {
      selectedProductId.value = productId;
      selectedView.value = 'product';
      fetchProductPredictions();
    };

    // ËÆ°ÁÆóÁªüËÆ°ÊåáÊ†á
    const calculateAverage = (data) => {
      if (!data || data.length === 0) return 0;
      return data.reduce((sum, val) => sum + val, 0) / data.length;
    };

    const calculateSum = (data) => {
      if (!data || data.length === 0) return 0;
      return data.reduce((sum, val) => sum + val, 0);
    };

    const calculateTrend = (data) => {
      if (!data || data.length < 2) return 0;
      const firstValue = data[0];
      const lastValue = data[data.length - 1];
      return ((lastValue - firstValue) / firstValue) * 100;
    };

    // Ê†ºÂºèÂåñÊó•Êúü
    const formatDate = (dateString) => {
      if (!dateString) return 'Êú™Áü•Êó•Êúü';
      try {
        const date = new Date(dateString);
        return `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}-${String(date.getDate()).padStart(2, '0')}`;
      } catch (e) {
        return 'Êó•ÊúüÊ†ºÂºèÈîôËØØ';
      }
    };

    // Ëé∑ÂèñËÆ¢Âçï‰∏≠ÁöÑ‰∫ßÂìÅÊï∞Èáè
    const getOrderQuantity = (order) => {
      return order.quantity || 1;
    };

    // Ëé∑ÂèñËÆ¢ÂçïÁä∂ÊÄÅÊñáÊú¨
    const getStatusText = (order) => {
      if (!order || !order.status) return 'Êú™Áü•Áä∂ÊÄÅ';

      const statusMap = {
        'PENDING': 'ÂæÖÂ§ÑÁêÜ',
        'PROCESSING': 'Â§ÑÁêÜ‰∏≠',
        'SHIPPED': 'Â∑≤ÂèëË¥ß',
        'DELIVERED': 'Â∑≤ÈÄÅËææ',
        'COMPLETED': 'Â∑≤ÂÆåÊàê',
        'CANCELLED': 'Â∑≤ÂèñÊ∂à'
      };

      return statusMap[order.status] || order.status;
    };

    // Ëé∑ÂèñËÆ¢ÂçïÁä∂ÊÄÅCSSÁ±ª
    const getStatusClass = (order) => {
      if (!order || !order.status) return '';

      const statusClassMap = {
        'PENDING': 'status-pending',
        'PROCESSING': 'status-processing',
        'SHIPPED': 'status-shipped',
        'DELIVERED': 'status-delivered',
        'COMPLETED': 'status-completed',
        'CANCELLED': 'status-cancelled'
      };

      return statusClassMap[order.status] || '';
    };

    return {
      // Ë°®ÂçïÂèÇÊï∞
      merchantId,
      selectedProductId,
      daysToPredict,
      selectedView,

      // Áä∂ÊÄÅÊéßÂà∂
      loading,
      loadingProducts,
      error,
      formError,
      showResults,

      // Êï∞ÊçÆ
      products,
      productOrders,
      predictionData,

      // ËÆ°ÁÆóÂ±ûÊÄß
      isFormValid,
      selectedProductName,
      chartData,
      chartOptions,

      // ÊñπÊ≥ï
      fetchProducts,
      startPrediction,
      viewProductPrediction,
      calculateAverage,
      calculateSum,
      calculateTrend,
      formatDate,
      getOrderQuantity,
      getStatusText,
      getStatusClass
    };
  }
};
</script>

