export default {
  props: {
    order: {
      type: Object,
      default: () => ({})
    },
    modelValue: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      visible: this.modelValue,
      form: {
        orderNumber: '',
        totalAmount: 0,
        orderStatus: 'pending'
      }
    };
  },
  watch: {
    order: {
      immediate: true,
      handler(newVal) {
        if (newVal) {
          this.form = {
            ...newVal
          };
        }
      }
    },
    modelValue: {
      immediate: true,
      handler(newVal) {
        this.visible = newVal;
      }
    }
  },
  methods: {
    submitForm() {
      const method = this.order ? 'PUT' : 'POST';
      const url = this.order ? `http://localhost:8080/api/orders/${this.order.orderId}` : 'http://localhost:8080/api/orders';
      fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(this.form)
      }).then(() => {
        this.$emit('refresh');
        this.handleClose();
      });
    },
    handleClose() {
      this.visible = false;
      this.$emit('close');
    }
  }
};
