import { ref, reactive, onMounted, onUnmounted, nextTick, computed } from 'vue';
import { ElMessage, ElNotification } from 'element-plus';
import { Refresh } from '@element-plus/icons-vue';
import * as echarts from 'echarts';
import axios from 'axios';
export default {
  name: 'AdminDataVisualizationView',
  components: {
    Refresh
  },
  setup() {
    // 图表引用
    const salesTrendChart = ref(null);
    const merchantRankingChart = ref(null);
    const productRankingChart = ref(null);
    const regionalDistributionChart = ref(null);
    const orderStatusDistributionChart = ref(null);

    // 图表实例
    let salesTrendChartInstance = null;
    let merchantRankingChartInstance = null;
    let productRankingChartInstance = null;
    let regionalDistributionChartInstance = null;
    let orderStatusDistributionChartInstance = null;

    // 数据状态
    const loading = reactive({
      dashboard: false,
      salesTrend: false,
      merchantRanking: false,
      productRanking: false,
      regionalDistribution: false,
      orderStatusDistribution: false
    });
    const timeFilter = ref('monthly');
    const timeFilterLabel = computed(() => {
      switch (timeFilter.value) {
        case 'daily':
          return '每日';
        case 'weekly':
          return '每周';
        case 'monthly':
          return '每月';
        default:
          return '每月';
      }
    });

    // 图表选择
    const selectedChartType = ref('all');

    // 用于判断图表是否显示
    const isChartVisible = chartType => {
      return selectedChartType.value === 'all' || selectedChartType.value === chartType;
    };

    // 处理图表类型变更
    const handleChartTypeChange = value => {
      // 重新调整图表大小
      nextTick(() => {
        if (value === 'all' || value === 'trend') {
          salesTrendChartInstance && salesTrendChartInstance.resize();
        }
        if (value === 'all' || value === 'merchant') {
          merchantRankingChartInstance && merchantRankingChartInstance.resize();
        }
        if (value === 'all' || value === 'product') {
          productRankingChartInstance && productRankingChartInstance.resize();
        }
        if (value === 'all' || value === 'region') {
          regionalDistributionChartInstance && regionalDistributionChartInstance.resize();
        }
        if (value === 'all' || value === 'status') {
          orderStatusDistributionChartInstance && orderStatusDistributionChartInstance.resize();
        }
      });

      // 通知用户
      ElNotification({
        title: '图表显示已更新',
        message: value === 'all' ? '显示所有图表' : `仅显示${getChartTypeName(value)}图表`,
        type: 'success',
        duration: 2000
      });
    };

    // 获取图表类型名称
    const getChartTypeName = chartType => {
      switch (chartType) {
        case 'trend':
          return '销售趋势';
        case 'merchant':
          return '商家销售排行';
        case 'product':
          return '产品销售排行';
        case 'region':
          return '区域销售分布';
        case 'status':
          return '订单状态分布';
        default:
          return '';
      }
    };
    const salesOverview = reactive({
      totalSales: 0,
      totalOrders: 0,
      avgOrderValue: 0,
      pendingOrders: 0,
      completedOrders: 0,
      recentSales: 0,
      merchantCount: 0,
      activeMerchantCount: 0
    });

    // 初始化图表
    const initCharts = () => {
      // 创建图表并应用主题
      const theme = {
        color: ['#409EFF', '#67C23A', '#E6A23C', '#F56C6C', '#909399', '#9254de', '#36cfc9', '#ff9c6e'],
        backgroundColor: 'rgba(252,252,252,0.6)',
        textStyle: {
          fontFamily: 'Arial, "Microsoft YaHei", sans-serif'
        },
        title: {
          textStyle: {
            color: '#303133',
            fontSize: 16,
            fontWeight: 'normal'
          },
          left: 'center'
        },
        legend: {
          textStyle: {
            color: '#606266'
          }
        },
        grid: {
          containLabel: true,
          left: '3%',
          right: '4%',
          bottom: '3%'
        }
      };
      echarts.registerTheme('ecmsTheme', theme);

      // 销售趋势图
      salesTrendChartInstance = echarts.init(salesTrendChart.value, 'ecmsTheme');

      // 商家销售排行图
      merchantRankingChartInstance = echarts.init(merchantRankingChart.value, 'ecmsTheme');

      // 产品销售排行图
      productRankingChartInstance = echarts.init(productRankingChart.value, 'ecmsTheme');

      // 区域销售分布图
      regionalDistributionChartInstance = echarts.init(regionalDistributionChart.value, 'ecmsTheme');

      // 订单状态分布图
      orderStatusDistributionChartInstance = echarts.init(orderStatusDistributionChart.value, 'ecmsTheme');

      // 监听窗口大小变化，调整图表大小
      window.addEventListener('resize', handleResize);
    };

    // 处理窗口大小变化
    const handleResize = () => {
      if (isChartVisible('trend')) {
        salesTrendChartInstance && salesTrendChartInstance.resize();
      }
      if (isChartVisible('merchant')) {
        merchantRankingChartInstance && merchantRankingChartInstance.resize();
      }
      if (isChartVisible('product')) {
        productRankingChartInstance && productRankingChartInstance.resize();
      }
      if (isChartVisible('region')) {
        regionalDistributionChartInstance && regionalDistributionChartInstance.resize();
      }
      if (isChartVisible('status')) {
        orderStatusDistributionChartInstance && orderStatusDistributionChartInstance.resize();
      }
    };

    // 获取仪表板数据
    // 获取仪表板数据
    // 获取仪表板数据
    const fetchDashboardData = async () => {
      try {
        loading.dashboard = true;

        // 销毁所有现有图表实例
        if (salesTrendChartInstance) {
          salesTrendChartInstance.dispose();
          salesTrendChartInstance = null;
        }
        if (merchantRankingChartInstance) {
          merchantRankingChartInstance.dispose();
          merchantRankingChartInstance = null;
        }
        if (productRankingChartInstance) {
          productRankingChartInstance.dispose();
          productRankingChartInstance = null;
        }
        if (regionalDistributionChartInstance) {
          regionalDistributionChartInstance.dispose();
          regionalDistributionChartInstance = null;
        }
        if (orderStatusDistributionChartInstance) {
          orderStatusDistributionChartInstance.dispose();
          orderStatusDistributionChartInstance = null;
        }

        // 获取新数据
        const response = await axios.get(`http://localhost:8080/api/admin/data-visualization/dashboard?period=${timeFilter.value}`);
        const dashboardData = response.data;

        // 更新销售概览数据
        Object.assign(salesOverview, dashboardData.salesOverview);

        // 等待DOM更新
        await nextTick();

        // 重新初始化所有图表实例
        if (salesTrendChart.value) {
          salesTrendChartInstance = echarts.init(salesTrendChart.value, 'ecmsTheme');
          if (dashboardData.salesTrend && Object.keys(dashboardData.salesTrend).length > 0) {
            renderSalesTrendChart(dashboardData.salesTrend);
          }
        }
        if (merchantRankingChart.value) {
          merchantRankingChartInstance = echarts.init(merchantRankingChart.value, 'ecmsTheme');
          if (dashboardData.merchantRanking && Object.keys(dashboardData.merchantRanking).length > 0) {
            renderMerchantRankingChart(dashboardData.merchantRanking);
          }
        }
        if (productRankingChart.value) {
          productRankingChartInstance = echarts.init(productRankingChart.value, 'ecmsTheme');
          if (dashboardData.productRanking && Object.keys(dashboardData.productRanking).length > 0) {
            renderProductRankingChart(dashboardData.productRanking);
          }
        }
        if (regionalDistributionChart.value) {
          regionalDistributionChartInstance = echarts.init(regionalDistributionChart.value, 'ecmsTheme');
          if (dashboardData.regionalDistribution && Object.keys(dashboardData.regionalDistribution).length > 0) {
            renderRegionalDistributionChart(dashboardData.regionalDistribution);
          }
        }
        if (orderStatusDistributionChart.value) {
          orderStatusDistributionChartInstance = echarts.init(orderStatusDistributionChart.value, 'ecmsTheme');
          if (dashboardData.orderStatusDistribution && Object.keys(dashboardData.orderStatusDistribution).length > 0) {
            renderOrderStatusDistributionChart(dashboardData.orderStatusDistribution);
          }
        }

        // 成功提示
        ElMessage({
          message: '数据已成功更新',
          type: 'success',
          duration: 2000
        });
      } catch (error) {
        console.error('获取仪表板数据失败', error);
        ElMessage({
          message: '获取仪表板数据失败: ' + (error.response?.data?.message || error.message),
          type: 'error'
        });
      } finally {
        loading.dashboard = false;
      }
    };

    // 渲染销售趋势图
    const renderSalesTrendChart = data => {
      const option = {
        title: {
          text: data.title || '平台销售趋势',
          subtext: `按${timeFilterLabel.value}统计`
        },
        tooltip: {
          trigger: 'axis',
          formatter: '{b}: {c} 元',
          backgroundColor: 'rgba(255,255,255,0.8)',
          borderColor: '#eee',
          borderWidth: 1,
          textStyle: {
            color: '#333'
          },
          extraCssText: 'box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);'
        },
        grid: {
          left: '3%',
          right: '4%',
          bottom: '3%',
          containLabel: true
        },
        xAxis: {
          type: 'category',
          data: data.xAxis || [],
          boundaryGap: false,
          axisTick: {
            alignWithLabel: true
          },
          axisLine: {
            lineStyle: {
              color: '#dcdfe6'
            }
          },
          axisLabel: {
            color: '#606266',
            rotate: data.xAxis && data.xAxis.length > 10 ? 30 : 0
          }
        },
        yAxis: {
          type: 'value',
          name: '销售额(元)',
          nameTextStyle: {
            color: '#606266'
          },
          axisLine: {
            show: true,
            lineStyle: {
              color: '#dcdfe6'
            }
          },
          splitLine: {
            lineStyle: {
              color: '#ebeef5'
            }
          }
        },
        series: [{
          name: '销售额',
          type: 'line',
          data: data.series || [],
          smooth: true,
          symbol: 'circle',
          symbolSize: 6,
          itemStyle: {
            color: '#409EFF'
          },
          lineStyle: {
            width: 3
          },
          areaStyle: {
            color: new echarts.graphic.LinearGradient(0, 0, 0, 1, [{
              offset: 0,
              color: 'rgba(64, 158, 255, 0.5)'
            }, {
              offset: 1,
              color: 'rgba(64, 158, 255, 0.1)'
            }])
          },
          markPoint: {
            data: [{
              type: 'max',
              name: '最大值'
            }, {
              type: 'min',
              name: '最小值'
            }]
          },
          markLine: {
            data: [{
              type: 'average',
              name: '平均值'
            }]
          }
        }]
      };
      salesTrendChartInstance.setOption(option);
    };

    // 渲染商家销售排行图
    const renderMerchantRankingChart = data => {
      const option = {
        title: {
          text: data.title || '商家销售排行',
          subtext: '按销售额排名'
        },
        tooltip: {
          trigger: 'axis',
          axisPointer: {
            type: 'shadow'
          },
          formatter: '{b}: {c} 元',
          backgroundColor: 'rgba(255,255,255,0.8)',
          borderColor: '#eee',
          borderWidth: 1,
          textStyle: {
            color: '#333'
          },
          extraCssText: 'box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);'
        },
        grid: {
          left: '3%',
          right: '4%',
          bottom: '3%',
          containLabel: true
        },
        xAxis: {
          type: 'value',
          name: '销售额(元)',
          nameTextStyle: {
            color: '#606266'
          },
          axisLine: {
            show: true,
            lineStyle: {
              color: '#dcdfe6'
            }
          },
          splitLine: {
            lineStyle: {
              color: '#ebeef5'
            }
          }
        },
        yAxis: {
          type: 'category',
          data: data.merchantNames || [],
          axisLabel: {
            interval: 0,
            rotate: 0,
            color: '#606266'
          },
          axisLine: {
            lineStyle: {
              color: '#dcdfe6'
            }
          }
        },
        visualMap: {
          min: 0,
          max: Math.max(...(data.salesValues || [0])),
          text: ['高', '低'],
          inRange: {
            color: ['#67C23A', '#409EFF']
          },
          calculable: true,
          orient: 'horizontal',
          left: 'center',
          bottom: '0%',
          show: false
        },
        series: [{
          name: '销售额',
          type: 'bar',
          data: data.salesValues || [],
          barWidth: '50%',
          itemStyle: {
            color: function (params) {
              const value = params.value;
              const max = Math.max(...(data.salesValues || [0]));
              return new echarts.graphic.LinearGradient(0, 0, 1, 0, [{
                offset: 0,
                color: '#67C23A'
              }, {
                offset: value / max,
                color: '#409EFF'
              }]);
            },
            borderRadius: [0, 4, 4, 0]
          },
          label: {
            show: true,
            position: 'right',
            formatter: '{c} 元'
          }
        }]
      };
      merchantRankingChartInstance.setOption(option);
    };

    // 渲染产品销售排行图
    const renderProductRankingChart = data => {
      const option = {
        title: {
          text: data.title || '产品销售排行',
          subtext: '按销售额排名'
        },
        tooltip: {
          trigger: 'axis',
          axisPointer: {
            type: 'shadow'
          },
          formatter: '{b}: {c} 元',
          backgroundColor: 'rgba(255,255,255,0.8)',
          borderColor: '#eee',
          borderWidth: 1,
          textStyle: {
            color: '#333'
          },
          extraCssText: 'box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);'
        },
        grid: {
          left: '3%',
          right: '4%',
          bottom: '3%',
          containLabel: true
        },
        xAxis: {
          type: 'value',
          name: '销售额(元)',
          nameTextStyle: {
            color: '#606266'
          },
          axisLine: {
            show: true,
            lineStyle: {
              color: '#dcdfe6'
            }
          },
          splitLine: {
            lineStyle: {
              color: '#ebeef5'
            }
          }
        },
        yAxis: {
          type: 'category',
          data: data.productNames || [],
          axisLabel: {
            interval: 0,
            rotate: 0,
            color: '#606266'
          },
          axisLine: {
            lineStyle: {
              color: '#dcdfe6'
            }
          }
        },
        series: [{
          name: '销售额',
          type: 'bar',
          data: data.salesValues || [],
          barWidth: '50%',
          itemStyle: {
            color: function (params) {
              const colorList = ['#67C23A', '#409EFF', '#E6A23C', '#F56C6C', '#909399'];
              return new echarts.graphic.LinearGradient(0, 0, 1, 0, [{
                offset: 0,
                color: '#E6A23C'
              }, {
                offset: 1,
                color: colorList[params.dataIndex % colorList.length]
              }]);
            },
            borderRadius: [0, 4, 4, 0]
          },
          label: {
            show: true,
            position: 'right',
            formatter: '{c} 元'
          }
        }]
      };
      productRankingChartInstance.setOption(option);
    };

    // 渲染区域销售分布图
    const renderRegionalDistributionChart = data => {
      const option = {
        title: {
          text: data.title || '区域销售分布',
          subtext: '按地区统计'
        },
        tooltip: {
          trigger: 'item',
          formatter: '{b}: {c} 元 ({d}%)',
          backgroundColor: 'rgba(255,255,255,0.8)',
          borderColor: '#eee',
          borderWidth: 1,
          textStyle: {
            color: '#333'
          },
          extraCssText: 'box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);'
        },
        legend: {
          type: 'scroll',
          orient: 'vertical',
          right: 10,
          top: 20,
          bottom: 20,
          textStyle: {
            color: '#606266'
          }
        },
        series: [{
          name: '区域销售',
          type: 'pie',
          radius: ['40%', '70%'],
          center: ['40%', '50%'],
          avoidLabelOverlap: true,
          itemStyle: {
            borderRadius: 4,
            borderColor: '#fff',
            borderWidth: 2
          },
          label: {
            show: false,
            position: 'center'
          },
          emphasis: {
            label: {
              show: true,
              fontSize: '18',
              fontWeight: 'bold'
            }
          },
          labelLine: {
            show: false
          },
          data: data.regions ? data.regions.map((region, index) => {
            return {
              name: region,
              value: data.values[index]
            };
          }) : []
        }]
      };
      regionalDistributionChartInstance.setOption(option);
    };

    // 渲染订单状态分布图
    const renderOrderStatusDistributionChart = data => {
      const option = {
        title: {
          text: data.title || '订单状态分布',
          subtext: '按状态统计'
        },
        tooltip: {
          trigger: 'item',
          formatter: '{a} <br/>{b}: {c} ({d}%)',
          backgroundColor: 'rgba(255,255,255,0.8)',
          borderColor: '#eee',
          borderWidth: 1,
          textStyle: {
            color: '#333'
          },
          extraCssText: 'box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);'
        },
        legend: {
          orient: 'vertical',
          left: 10,
          top: 'center',
          textStyle: {
            color: '#606266'
          }
        },
        series: [{
          name: '订单状态',
          type: 'pie',
          radius: ['40%', '70%'],
          avoidLabelOverlap: false,
          itemStyle: {
            borderRadius: 4,
            borderColor: '#fff',
            borderWidth: 2
          },
          label: {
            show: false,
            position: 'center'
          },
          emphasis: {
            label: {
              show: true,
              fontSize: '18',
              fontWeight: 'bold'
            }
          },
          labelLine: {
            show: false
          },
          data: data.data ? data.data.map(item => {
            // 为不同状态设置不同颜色
            const colorMap = {
              '待处理': '#E6A23C',
              '已发货': '#409EFF',
              '已完成': '#67C23A',
              '已退款': '#F56C6C'
            };
            return {
              ...item,
              itemStyle: {
                color: colorMap[item.name] || undefined
              }
            };
          }) : []
        }]
      };
      orderStatusDistributionChartInstance.setOption(option);
    };

    // 格式化货币
    const formatCurrency = value => {
      if (value === undefined || value === null) return '¥0.00';
      return '¥' + parseFloat(value).toFixed(2).replace(/\B(?=(\d{3})+(?!\d))/g, ','); // 添加千位分隔符
    };

    // 组件挂载
    onMounted(async () => {
      await nextTick();
      initCharts();
      fetchDashboardData();

      // 显示欢迎提示
      ElNotification({
        title: '数据仪表板已加载',
        message: '欢迎使用平台数据可视化系统',
        type: 'success',
        duration: 3000
      });
    });

    // 组件卸载
    onUnmounted(() => {
      // 销毁图表实例，避免内存泄漏
      salesTrendChartInstance && salesTrendChartInstance.dispose();
      merchantRankingChartInstance && merchantRankingChartInstance.dispose();
      productRankingChartInstance && productRankingChartInstance.dispose();
      regionalDistributionChartInstance && regionalDistributionChartInstance.dispose();
      orderStatusDistributionChartInstance && orderStatusDistributionChartInstance.dispose();

      // 移除窗口大小变化监听
      window.removeEventListener('resize', handleResize);
    });
    return {
      salesTrendChart,
      merchantRankingChart,
      productRankingChart,
      regionalDistributionChart,
      orderStatusDistributionChart,
      loading,
      timeFilter,
      timeFilterLabel,
      salesOverview,
      fetchDashboardData,
      formatCurrency,
      selectedChartType,
      isChartVisible,
      handleChartTypeChange
    };
  }
};
